package main

import (
	"fmt"
	"strings"
)

func main() {
	fmt.Println("Welcome to gobio!")
	dna := ""

		match := strings.Contains(dna, "GAAACCATTATTATCA")
	// false match := strings.Contains(dna, "GAAACCATATTATCA")
	fmt.Println(match)
	x := longestMatch(dna, "GAAACCATTATTATCA")
	fmt.Println(x)
	
}

func longestMatch(seq, subseq string) int {
	sub := ""
	longest := 0
	for idx, s := range subseq {
		sub += string(s)
		b := strings.Contains(seq, sub)
		if b {
			longest++
		}
		fmt.Printf(" %d. %s: %v\n", idx, sub, b)
	}
	return longest
}
